const file = 'step_through_property_set_test.dart';

void code() {
  final bar = Bar();
  bar.doStuff();
}

class Foo {
  final List<String> data1;

  Foo() : data1 = ['a', 'b', 'c'];

  void doStuff() {
    data1[1] = 'x';
    print(data1[1]);
  }
}

class Bar extends Foo {
  final List<String> data2;

  Bar() : data2 = ['d', 'e', 'f'];

  void doStuff() {
    data2[1] = '1'; // LINE_A
    print(data2[1]);

    data1[1] = '2';
    print(data1[1]);

    super.data1[1] = '42';
    print(super.data1[1]);
  }
}

final stops = <String>[];
const expected = <String>[
  '$file:${LINE_A + 0}:5', // on 'data2'
  '$file:${LINE_A + 0}:10', // on '['
  '$file:${LINE_A + 1}:11', // on 'data2'
  '$file:${LINE_A + 1}:16', // on '['
  '$file:${LINE_A + 1}:5', // on 'print'

  '$file:${LINE_A + 3}:5', // on 'data1'
  '$file:${LINE_A + 3}:10', // on '['
  '$file:${LINE_A + 4}:11', // on 'data1'
  '$file:${LINE_A + 4}:16', // on '['
  '$file:${LINE_A + 4}:5', // on 'print'

  '$file:${LINE_A + 6}:11', // on 'data1'
  '$file:${LINE_A + 6}:16', // on '['
  '$file:${LINE_A + 7}:17', // on 'data1'
  '$file:${LINE_A + 7}:22', // on '['
  '$file:${LINE_A + 7}:5', // on 'print'

  '$file:${LINE_A + 8}:3' // on ending '}'
];

final tests = <IsolateTest>[
  hasPausedAtStart,
  setBreakpointAtLine(LINE_A),
  runStepIntoThroughProgramRecordingStops(stops),
  checkRecordedStops(
    stops,
    expected,
    debugPrint: true,
    debugPrintFile: file,
    debugPrintLine: LINE_A,
  ),
];

void main([args = const <String>[]]) => runIsolateTests(
      args,
      tests,
      'step_through_property_set_test.dart',
      testeeConcurrent: code,
      pause_on_start: true,
      pause_on_exit: true,
    );
